mapper.select:
select user_id,touser_id,create_time,
(select name from customer_company where customer_company.company_id=(select company_id from customer_user where customer_user.user_id=card_collect.touser_id)) as touserComName,
(select avatar from customer_user where customer_user.user_id=card_collect.touser_id) as touserAvatar,
(select realname from customer_user where customer_user.user_id=card_collect.touser_id) as touserName
from card_collect
WHERE user_id=#{userId}


        order by ${sidx} ${order}


        order by collect_id desc



    limit #{offset}, #{limit}
