mapper.select:
select user_id,touser_id,create_time,
(select name from customer_company where customer_company.company_id=(select company_id from card_user where card_user.user_id=card_record.touser_id)) as touserComName,
(select openid from customer_user where customer_user.user_id=card_record.touser_id) as openid,
(select photo from card_user where card_user.user_id=card_record.touser_id) as touserAvatar,
(select realname from card_user where card_user.user_id=card_record.touser_id) as touserName
from card_record
WHERE user_id=#{userId}


        order by ${sidx} ${order}


        order by record_id desc



    limit #{offset}, #{limit}
